import React, { Fragment } from 'react'
import FeedItem from 'components/feeditem'

const Feed = ({articles}) => {
    if(articles.length < 1) {
        return ''
    }
    return (
        <Fragment>
           {articles.length && articles.map((a,itx) => (
                <FeedItem article={a} key={itx} />
            )) }
        </Fragment>
    )
}

export default Feed
