import React from 'react'
import {Link} from 'react-router-dom'

const FeedItem = ({article}) => {
    return (
        <div className="feed-item">
            <div className="feed-item-user">
                <img src={article.author.image} alt=""/>
                <div className="updated-details">
                    <span className="updated-user">
                        <Link to={`/profile/${article.author.username}`}>
                            {article.author.username}
                        </Link>
                    </span>
                    <span className="updated-date">{article.updatedAt}</span>
                </div>
            </div>
            <div className="feed-item-content">
                <h3>{article.title}</h3>
                <div className="feed-content">
                    {article.description}
                </div>
                <div className="feed-content-details">
                    <Link className="feed-readmore" to={`/articles/${article.slug}`}>Readmore ...</Link>
                    <ul className="feed-tag">
                        {
                            article.taglist && article.taglist.map(t => (<li>{t}</li>))
                        }
                    </ul>
                </div>
            </div>
        </div>
    )
}

export default FeedItem
