import React from 'react'
import {NavLink} from 'react-router-dom'
const FeedToggle = ({tagname}) => {
    return (
        <div className="feed-toggler">
            <ul className="toggle-items">
                <li className="toggle-item">
                    <NavLink to={`/feeds`}>Your Feeds</NavLink>
                </li>
                <li className="toggle-item">
                    <NavLink to={`/`} exact>Global Feeds</NavLink>
                </li>
                {tagname && <li className="toggle-item">
                    <NavLink to={`/tags/${tagname}`}>{tagname}</NavLink>
                </li>}
            </ul>
        </div>
    )
}

export default FeedToggle
