import React, {useContext} from 'react'
import {NavLink} from 'react-router-dom'
import {CurrentUserContext} from 'contexts/currentUser'

const FeedToggle = ({tagname}) => {
    const [currentUser] = useContext(CurrentUserContext)
    return (
        <div className="feed-toggler">
            <ul className="toggle-items">
                {currentUser.isLoggedIn && <li className="toggle-item">
                    <NavLink to={`/feed`}>Your Feeds</NavLink>
                </li>}
                <li className="toggle-item">
                    <NavLink to={`/`} exact>Global Feeds</NavLink>
                </li>
                {tagname && <li className="toggle-item">
                    <NavLink to={`/tags/${tagname}`}>{tagname}</NavLink>
                </li>}
            </ul>
        </div>
    )
}

export default FeedToggle
