import React, { Fragment, useState, useEffect, useContext } from 'react'
import {Redirect} from 'react-router-dom'
import ArticleForm from 'components/articleform'
import useFetch from 'hooks/useFetch'
import {CurrentUserContext} from 'contexts/currentUser'

const CreateArticle = () => {
    const apiUrl = '/articles'
    const [{ response, error, isLoading }, doFetch] = useFetch(apiUrl)
    const [isSuccess, setIsSuccess] = useState(false)
    const [userState, dispatch] = useContext(CurrentUserContext)

    const initialValues = {
        title: '',
        description: '',
        body: '',
        taglist:''
    }

    const onSubmitHandler = article =>{
        doFetch({
            method: 'post',
            data: {
                article
            }
        })
    }

    useEffect(() => {
        if(!response) {
            return false
        }
        setIsSuccess(true)
    }, [response])

    if(!userState) {
        return null;
    }

    if(isSuccess || !userState.isLoggedIn) {
        return <Redirect to='/' />
    }

    return (
        <Fragment>
            <ArticleForm initialValues={initialValues} onSubmitHandler={onSubmitHandler} errors={(error && error.errors) || []} />
        </Fragment>
    )
}

export default CreateArticle
