import {parse} from 'query-string'

export const range = (start, end) => [...Array(end).keys()].map(el => el + start)

export const LIMIT = 10

export const getPageQueryForApi = (search) => {
    const parsedQueryString = parse(search)
    const currentPage = parsedQueryString.page? Number(parsedQueryString.page) : 1
    const offset = currentPage * LIMIT - LIMIT
    return {currentPage, offset}
}